from collections import deque

class Dinic:
    class Edge:
        def __init__(self, to, rev, c, oc):
            self.to = to
            self.rev = rev
            self.c = c
            self.oc = oc

        def flow(self):
            return max(self.oc - self.c, 0)

    def __init__(self, n):
        self.lvl = [0] * n
        self.ptr = [0] * n
        self.q = [0] * n
        self.adj = [[] for _ in range(n)]

    def add_edge(self, a, b, c, rcap=0):
        self.adj[a].append(self.Edge(b, len(self.adj[b]), c, c))
        self.adj[b].append(self.Edge(a, len(self.adj[a]) - 1, rcap, rcap))

    def dfs(self, v, t, f):
        if v == t or f == 0:
            return f
        for i in range(self.ptr[v], len(self.adj[v])):
            e = self.adj[v][i]
            if self.lvl[e.to] == self.lvl[v] + 1:
                p = self.dfs(e.to, t, min(f, e.c))
                if p:
                    e.c -= p
                    self.adj[e.to][e.rev].c += p
                    return p
        return 0

    def calc(self, s, t):
        flow = 0
        self.q[0] = s
        for L in range(31):
            self.lvl = [0] * len(self.lvl)
            self.ptr = [0] * len(self.ptr)
            qi = 0
            qe = self.lvl[s] = 1
            while qi < qe and not self.lvl[t]:
                v = self.q[qi]
                qi += 1
                for e in self.adj[v]:
                    if not self.lvl[e.to] and e.c >> (30 - L):
                        self.q[qe] = e.to
                        qe += 1
                        self.lvl[e.to] = self.lvl[v] + 1
            while (p := self.dfs(s, t, float('inf'))):
                flow += p
        return flow

    def left_of_min_cut(self, a):
        return self.lvl[a] != 0

def main():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    n = int(data[0])
    grid = [[0] * n for _ in range(n)]
    d = Dinic(2 * n + 2)
    edges = []

    for i in range(n):
        str_row = data[i + 1]
        for j in range(n):
            if str_row[j] == 'o':
                d.add_edge(i, j + n, 1)
                edges.append((i, j + n))
        d.add_edge(2 * n, i, 1)
        d.add_edge(i + n, 2 * n + 1, 1)

    mf = d.calc(2 * n, 2 * n + 1)
    moves = [False] * (2 * n)

    for l, r in edges:
        if d.left_of_min_cut(l):
            moves[r] = True
        else:
            moves[l] = True

    cnt = sum(moves)
    print(cnt)

    for i in range(2 * n):
        if moves[i]:
            print(2 if i >= n else 1, (i % n) + 1)

