class Node:
    def __init__(self, lo, hi):
        self.l = None
        self.r = None
        self.lo = lo
        self.hi = hi
        self.madd = 0
        self.val = 0
        self.reset = False
        if lo + 1 < hi:
            mid = lo + (hi - lo) // 2
            self.l = Node(lo, mid)
            self.r = Node(mid, hi)
            self.val = self.l.val + self.r.val

    def query(self, L, R):
        if R <= self.lo or self.hi <= L:
            return 0
        if L <= self.lo and self.hi <= R:
            return self.val
        self.push()
        return self.l.query(L, R) + self.r.query(L, R)

    def add(self, L, R, x):
        if R <= self.lo or self.hi <= L:
            return
        if L <= self.lo and self.hi <= R:
            self.madd += x
            self.val += x
        else:
            self.push()
            self.l.add(L, R, x)
            self.r.add(L, R, x)
            self.val = self.l.val + self.r.val

    def clear(self, L, R):
        if R <= self.lo or self.hi <= L:
            return
        if L <= self.lo and self.hi <= R:
            self.reset = True
            self.madd = 0
        else:
            self.push()
            self.l.clear(L, R)
            self.r.clear(L, R)
            self.val = self.l.val + self.r.val

    def push(self):
        if not self.l:
            mid = self.lo + (self.hi - self.lo) // 2
            self.l = Node(self.lo, mid)
            self.r = Node(mid, self.hi)
        if self.reset:
            self.l.clear(self.lo, self.hi)
            self.r.clear(self.lo, self.hi)
            self.reset = False
        if self.madd:
            self.l.add(self.lo, self.hi, self.madd)
            self.r.add(self.lo, self.hi, self.madd)
            self.madd = 0

