import sys
import cmath
from collections import deque

def fft(a):
    n = len(a)
    L = 31 - (n - 1).bit_length()
    R = [1] * (2 * n)
    rt = [1] * (2 * n)

    for k in range(2, n * 2, k * 2):
        x = cmath.exp(complex(0, cmath.pi) / k)
        for i in range(k, 2 * k):
            rt[i] = R[i] = R[i // 2] * x if i & 1 else R[i // 2]

    rev = [0] * n
    for i in range(n):
        rev[i] = (rev[i // 2] | (i & 1) << L) // 2

    for i in range(n):
        if i < rev[i]:
            a[i], a[rev[i]] = a[rev[i]], a[i]

    for k in range(1, n, k * 2):
        for i in range(0, n, 2 * k):
            for j in range(k):
                z = rt[j + k] * a[i + j + k]
                a[i + j + k] = a[i + j] - z
                a[i + j] += z

def conv(a, b):
    if not a or not b:
        return []
    res = [0] * (len(a) + len(b) - 1)
    L = 32 - (len(res) - 1).bit_length()
    n = 1 << L
    in_data = [0] * n
    out_data = [0] * n

    for i in range(len(a)):
        in_data[i] = complex(a[i], 0)
    for i in range(len(b)):
        in_data[i].imag = b[i]

    fft(in_data)

    for i in range(n):
        in_data[i] *= in_data[i]

    for i in range(n):
        out_data[i] = in_data[-i & (n - 1)] - in_data[i].conjugate()

    fft(out_data)

    for i in range(len(res)):
        res[i] = out_data[i].imag / (4 * n)

    return res

def main():
    input = sys.stdin.read
    str_input = input().strip()
    n = len(str_input)
    cnt = sum(1 for char in str_input if char == '1')

    left = [0] * cnt
    right = [0] * cnt
    pre = 0
    idx = 0

    for i in range(n):
        if str_input[i] == '1':
            left[idx] = i - pre + 1
            pre = i + 1
            idx += 1

    idx = cnt - 1
    pre = n - 1

    for i in range(n - 1, -1, -1):
        if str_input[i] == '1':
            right[idx] = pre - i + 1
            pre = i - 1
            idx -= 1

    right.reverse()
    res = conv(left, right)
    res = res[:cnt][::-1]

    ans = [0] * (n + 1)

    for i in range(len(res)):
        ans[i + 1] = int(res[i] + 0.1)

    ones = [-1]
    for i in range(n):
        if str_input[i] == '1':
            ones.append(i)
    ones.append(n)

    for i in range(len(ones) - 1):
        v = ones[i + 1] - ones[i]
        ans[0] += v * (v - 1) // 2

    print(" ".join(map(str, ans)))

if __name__ == "__main__":
    main()

