import random
from typing import List, Tuple, Optional

class Node:
    def __init__(self, val: int):
        self.l: Optional[Node] = None
        self.r: Optional[Node] = None
        self.val = val
        self.y = random.randint(0, 2**30)
        self.c = 1
        self.mn = (val, 0)
        self.sw = False

    def recalc(self):
        self.c = cnt(self.l) + cnt(self.r) + 1
        self.mn = (self.val, cnt(self.l))
        if self.l:
            self.mn = min(self.mn, self.l.mn)
        if self.r:
            self.mn = min(self.mn, (self.r.mn[0], self.r.mn[1] + 1 + cnt(self.l)))

    def tag(self):
        self.sw = not self.sw
        self.mn = (self.mn[0], cnt(self) - 1 - self.mn[1])
        self.l, self.r = self.r, self.l

    def push(self):
        if self.sw:
            self.sw = False
            if self.l:
                self.l.tag()
            if self.r:
                self.r.tag()

def cnt(n: Optional[Node]) -> int:
    return n.c if n else 0

def each(n: Optional[Node], f):
    if n:
        n.push()
        each(n.l, f)
        f(n)
        each(n.r, f)

def split(n: Optional[Node], k: int) -> Tuple[Optional[Node], Optional[Node]]:
    if not n:
        return None, None
    n.push()
    if cnt(n.l) >= k:
        pa = split(n.l, k)
        n.l = pa[1]
        n.recalc()
        return pa[0], n
    else:
        pa = split(n.r, k - cnt(n.l) - 1)
        n.r = pa[0]
        n.recalc()
        return n, pa[1]

def merge(l: Optional[Node], r: Optional[Node]) -> Optional[Node]:
    if not l:
        return r
    if not r:
        return l
    l.push()
    r.push()
    if l.y > r.y:
        l.r = merge(l.r, r)
        l.recalc()
        return l
    else:
        r.l = merge(l, r.l)
        r.recalc()
        return r

def ins(t: Optional[Node], n: Node, pos: int) -> Optional[Node]:
    pa = split(t, pos)
    return merge(merge(pa[0], n), pa[1])

def rev(t: Optional[Node], l: int, r: int):
    a, b, c = split(t, l), split(split(t, l)[1], r - l)
    b[0].tag()
    t = merge(a[0], merge(b[0], c[1]))

def main():
    n = int(input())
    root = None
    for i in range(n):
        v = int(input())
        root = ins(root, Node(v), i)

    ans = []
    for i in range(n):
        mindex = root.mn[1]
        ans.append((i + 1, i + 1 + mindex))
        rev(root, 0, mindex + 1)
        p, root = split(root, 1)

    print(len(ans))
    for a, b in ans:
        print(a, b)

if __name__ == "__main__":
    main()

