from collections import defaultdict
import heapq

class UF:
    def __init__(self, n, r):
        self.e = [-1] * n
        self.ss = [set() for _ in range(n)]
        for i in range(n):
            self.ss[i].add((r[i], i))

    def find(self, x):
        if self.e[x] < 0:
            return x
        self.e[x] = self.find(self.e[x])
        return self.e[x]

    def same_set(self, a, b):
        return self.find(a) == self.find(b)

    def size(self, x):
        return -self.e[self.find(x)]

    def join(self, a, b):
        a = self.find(a)
        b = self.find(b)
        if a == b:
            return False
        if self.e[a] > self.e[b]:
            a, b = b, a
        self.e[a] += self.e[b]
        self.e[b] = a
        for R, i in self.ss[b]:
            self.ss[a].add((R, i))
        self.ss[b].clear()
        return True

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    p = list(map(lambda x: int(x) - 1, data[1:n + 1]))

    l = [-1] * n
    r = [-1] * n
    T = 0
    solve = 0
    seen = [False] * n

    for i in range(n):
        seen[p[i]] = True
        l[p[i]] = T
        T += 1
        while solve < n and seen[solve]:
            r[solve] = T
            solve += 1

    adj = defaultdict(list)
    uf = UF(n, r)
    rs = [(l[i], r[i], i) for i in range(n)]
    rs.sort()
    pq = []

    for L, R, i in rs:
        while pq and pq[0][0] < L:
            r1, idx1 = heapq.heappop(pq)
            u = uf.find(idx1)
            it = next(((R, i) for R, i in uf.ss[u] if R > r1), None)
            if it:
                heapq.heappush(pq, it)

        other = []
        while pq and pq[0][0] < R:
            r1, idx = heapq.heappop(pq)
            other.append((r1, idx))

        for j in range(1, len(other)):
            uf.join(other[j - 1][1], other[j][1])

        if other:
            adj[i].append(other[0][1])
            adj[other[0][1]].append(i)
            u = uf.find(other[0][1])
            it = next(((L, -1) for R, i in uf.ss[u] if R > L), None)
            if it:
                heapq.heappush(pq, it)

        heapq.heappush(pq, (R, i))

    comps = defaultdict(list)
    for i in range(n):
        comps[uf.find(i)].append(i)

    state = [0] * n
    val = [0] * n

    def dfs(c, t):
        if state[c]:
            return val[c] != t
        state[c] = 1
        val[c] = t
        for u in comps[c]:
            for v in adj[u]:
                if dfs(uf.find(v), not t):
                    return True
        return False

    imp = False
    for i in range(n):
        if comps[i] and state[i] == 0 and dfs(i, 0):
            imp = True

    if imp:
        print("IMPOSSIBLE")
        return

    st = [[], []]
    solve = 0
    for i in range(n):
        v = p[i]
        c = val[uf.find(v)]
        if st[c] and st[c][-1] < v:
            imp = True
            break
        st[c].append(v)
        while solve < n and ((st[0] and st[0][-1] == solve) or (st[1] and st[1][-1] == solve)):
            if st[0] and st[0][-1] == solve:
                st[0].pop()
            else:
                st[1].pop()
            solve += 1

    if solve != n:
        imp = True

    if imp:
        print("IMPOSSIBLE")
    else:
        print(" ".join(str(val[uf.find(p[i])] + 1) for i in range(n)))

if __name__ == "__main__":
    main()
